import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom'
import { connect } from 'react-redux';
/** @jsx jsx */
import { jsx } from '@emotion/react';
import { ListGroup, ListGroupItem } from 'reactstrap';
import * as actionType from '../../store/actions';

const UserView = (props) => {
    const currentUserId = useParams().id;
    const [userData, setUserDate] = useState(); 

    useEffect(() => {
        async function getSingleUser(){
            try {
                await fetch(`/api/v2/users/${currentUserId}`, {
                    headers: {
                        method:'GET',
                        authorization: '123abc456def789ghi'
                        //props.token,
                    }
                })
                .then(
                    (res) => {
                        setUserDate(JSON.parse(res._bodyInit).users);
                        console.log(JSON.parse(res._bodyInit).users)
                    }
                )
            }
            catch (ex) {
                console.log(ex);
            }
        }
        getSingleUser();
    }, []);


    return (
        <div
            css={{
                backgroundColor:'#fcf8e8',
                height: "100vh",
                width: '100vw',
                padding: '50px'
            }}
        >
            <h3 css={{color: '#df7861'}}>Single User details</h3>
            {console.log(userData)}
            <ListGroup>
                <ListGroupItem>id: {userData.id}</ListGroupItem>
                <ListGroupItem>Email: {userData.email}</ListGroupItem>
                <ListGroupItem>Job count: {userData.jobs_count}</ListGroupItem>
                <ListGroupItem>Active: {userData.active.toString()}</ListGroupItem>
            </ListGroup>
        </div>
    )
}


const mapStateToProps = state => {
    return {
      token: state.authReducer.token,
      userId: state.userReducer.currentUserId
    }
  }



export default connect(mapStateToProps)(UserView);
