import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import { Table, Button } from 'reactstrap';
/** @jsx jsx */
import { jsx } from '@emotion/react';
import * as actionType from '../../store/actions';

const UsersList = (props) => {
    const [users, setUsers] = useState([]);
    useEffect(() => {
        async function getUsers(){
            try {
                fetch('/api/v2/users', {
                    headers: {
                      Authorization: props.token,
                    }
                })
                .then(
                    (res) => {
                        props.setUserList(JSON.parse(res._bodyText).users);
                        setUsers(JSON.parse(res._bodyText).users.map(
                            item => {
                                item.key = item.id;
                                return item
                            }
                        ));
                    }
                )
            }
            catch (ex) {
                console.log(ex);
            }
        }
        getUsers();
    }, []);

    const handleNewUser = () => {
        window.location = "/users/create";
    }


    return (
        <div
            css={{
                backgroundColor:'#fcf8e8',
                height: "100vh",
                width: '100vw',
                padding: '50px'
            }}
        >
            <div css={{padding:'20px'}}>
                <h3 css={{color: '#df7861', display: 'inline'}}>User Table</h3>
                <Button 
                    onClick={handleNewUser}
                    outline
                    css={{
                        display: 'inline',
                        float: 'right',
                        color: '#df7861'
                    }}
                >Create a new user</Button>
            </div>
            <Table bordered hover>
                <thead>
                    <tr>
                    <th>ID</th>
                    <th>Email</th>
                    <th>Jobs Count</th>
                    <th>Active</th>
                    <th></th>
                    <th></th>
                    <th></th>
                    </tr>
                </thead>
                <tbody>
                {users.map((item) => {
                    return (
                        <tr>
                            <td>{item.id}</td>
                            <td>{item.email}</td>
                            <td>{item.jobs_count}</td>
                            <td>{item.active.toString()}</td>
                            <td><Link to={{pathname:'/users/${item.id}'}}>View</Link></td>
                            <td><a>Edit</a></td>
                            <td><a>Delete</a></td>
                        </tr>
                    )
                })}
                </tbody>
            </Table>
        </div>
    )

}

const mapStateToProps = state => {
    return {
      userToken: state.authReducer.token,
      userList: state.userReducer.users
    }
  }

const mapDispatchToProps = dispatch => {
    return {
        setUserList: (data) => dispatch({type: actionType.USERLIST, users:data})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UsersList);
