import React, { useState, useEffect } from "react";
import { connect } from 'react-redux';
import { Table } from 'reactstrap';
import * as actionType from '../../store/actions';

const UsersList = (props) => {
    const [users, setUsers] = useState([]);
    useEffect(() => {
        async function getUsers(){
            try {
                fetch('/api/v2/users', {
                    headers: {
                      Authorization: props.token,
                    }
                })
                .then(
                    (res) => {
                        props.setUserList(JSON.parse(res._bodyText).users);
                        setUsers(JSON.parse(res._bodyText).users);
                    }
                )
            }
            catch (ex) {
                console.log(ex);
            }
        }
        getUsers();
    }, []);


    return (
        <div>
            <h2>User Table</h2>
            <Table>
                <thead>
                    <tr>
                    <th>ID</th>
                    <th>Email</th>
                    <th>Jobs Count</th>
                    <th>Active</th>
                    <th></th>
                    <th></th>
                    <th></th>
                    </tr>
                </thead>
                <tbody>
                {users.map((item) => {
                    return (
                        <tr>
                            <td>{item.id}</td>
                            <td>{item.email}</td>
                            <td>{item.jobs_count}</td>
                            <td>{item.active}</td>
                            <td><link></link>View</td>
                            <td><link></link>Edit</td>
                            <td><link></link>Delete</td>
                        </tr>
                    )
                })}
                </tbody>
            </Table>
        </div>
    )

}

const mapStateToProps = state => {
    return {
      userToken: state.authReducer.token,
      userList: state.userReducer.users
    }
  }

const mapDispatchToProps = dispatch => {
    return {
        setUserList: (data) => dispatch({type: actionType.USERLIST, users:data})
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(UsersList);
