import * as actionTypes from '../actions';

const initialState = {
    users: [],
    currentUserId: ''
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.USERLIST:
            return {
                ...state,
                user: action.users
            }
        case actionTypes.SETCURRENTUSER:
            return {
                ...state,
                currentUserId: action.userId
            }
    }
    return state;
}

export default reducer;
