import React from "react";
import { useFormik } from 'formik';
import { connect } from 'react-redux';
/** @jsx jsx */
import { jsx } from '@emotion/react';
import { Button, Form, FormGroup, Label, Input, Col } from 'reactstrap';
//import server from '../../mockServer/index';
import * as actionType from '../../store/actions'


const Login = (props) => {

  const formik = useFormik({
    initialValues: {
      email: '',
      password: ''
    },
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
      fetch('/api/v2/users/tokens', {
        method: 'POST',
        body: JSON.stringify(values)
      })
      .then(
        res => props.onLogin(res.headers.map.authorization)
      );
    }
  });

  // const validate = (values) => {
  //   let errors = {};
  //   const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
  //   if (!values.email) {
  //     errors.email = "Cannot be blank";
  //   } else if (!regex.test(values.email)) {
  //     errors.email = "Invalid email format";
  //   }
  //   if (!values.password) {
  //     errors.password = "Cannot be blank";
  //   } else if (values.password.length < 4) {
  //     errors.password = "Password must be more than 4 characters";
  //   }
  //   return errors;
  // };

  return (
    <div 
      css={{
        backgroundColor:'#bbdfc8',
        height: "100vh",
        width: '100vw',
        textAlign: 'center',
        display:'flex',
        flexDirection: 'column',
        alignItems: 'center'
      }}>
      <Form
        onSubmit={formik.handleSubmit}
        css={{
          border:'solid 2px gray',
          borderRadius: '8px',
          width: '400px',
          padding: '20px 40px',
          display: 'flex',
          flexDirection: 'column',
          margin: 'auto',
          backgroundColor: '#f0e5d8',
          boxShadow: '0 10px 50px #555',
          '@media(max-width: 600px)': {
            width: '100%',
            height:'100%',
            padding: '20px 40px',
            boxShadow: '0 0 0 #fff',
            borderRadius: '0px',
            border:'0px',
          }
        }}
      >
        <h2
          css={{paddingBottom: '20px'}}
        >Welcome</h2>
        <FormGroup row>
          <Label sm={3} for="email">Email</Label>
          <Col sm={9}>
            <Input
              id="email"
              name="email"
              type="email"
              placeholder="Please enter your email"
              onChange={formik.handleChange}
              value={formik.values.email}
            />
          </Col>
        </FormGroup>
        <FormGroup row>
          <Label sm={3} for="password">Password</Label> 
          <Col sm={9}>
            <Input
              id="password"
              name="password"
              type="password"
              placeholder="Please enter your password"
              onChange={formik.handleChange}
              value={formik.values.password}
            />
          </Col>
        </FormGroup>
        <Button type="submit">Submit</Button>
      </Form>
    </div>
  )


}

const mapStateToProps = state => {
  return {
    token: state.authReducer.token
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onLogin: (token) => dispatch({type: actionType.LOGIN, token:token})
  }
}
console.log(props.token);
export default connect(null,mapDispatchToProps)(Login);
